import React from "react";
import Card from "react-bootstrap/Card";

function BookingCardComponent({ booking }) {
  const { booking_id, flight_id, passenger_id, booking_date, status } = booking;

  return (
    <Card style={{ width: "18rem", margin: "1rem" }}>
      <Card.Body>
        <Card.Title>Booking ID: {booking_id}</Card.Title>
        <Card.Text>
          <strong>Flight ID:</strong> {flight_id}
          <br />
          <strong>Passenger ID:</strong> {passenger_id}
          <br />
          <strong>Booking Date:</strong>{" "}
          {new Date(booking_date).toLocaleString()}
          <br />
          <strong>Status:</strong> {status} <br />
          <strong>
          <button><img src="../../public/trash-can.png" alt="delete"/></button>
          </strong>
        </Card.Text>
      </Card.Body>
    </Card>
  );
}

export default BookingCardComponent;
