import React, { useEffect, useState } from "react";
import CardComponent from "./components/CardComponent";
import HeaderComponent from "./components/header";
import "./styles/app.css";
import {
  Container,
  Navbar,
  Nav,
  Form,
  FormControl,
  Button,
} from "react-bootstrap";

function App() {
  const [flights, setFlights] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetch("http://localhost:3000/flights") // Adjust the URL to your JSON server
      .then((response) => response.json())
      .then((data) => setFlights(data.filter((flight) => flight.id)))
      .catch((error) => console.error("Error fetching flights:", error));
  }, []);

  const handleSearch = (event) => {
    // setSearchTerm(event.target.value);
  };

  const handleAddFlight = () => {
    // Implement logic for adding a new flight
    // Redirect to a new page or modal for adding flight details
  };

  return (
    <div>
      <div>
        <HeaderComponent />
      </div>
      <Navbar expand="lg" className="bg-body-tertiary">
        <Container fluid>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="me-auto my-2 my-lg-0"
              style={{ maxHeight: "100px" }}
              navbarScroll
            >
              <Nav.Link href="#action1">Home</Nav.Link>
            </Nav>
            <Form className="d-flex">
              <Form.Control
                type="search"
                placeholder="Search by Flight ID"
                className="me-2"
                aria-label="Search"
              />
              <Button variant="outline-success">Search</Button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <Navbar expand="lg" className="bg-body-tertiary">
        <Container>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link onClick={handleAddFlight}>Add Flight</Nav.Link>
            </Nav>
            <Form className="d-flex">
              <FormControl
                type="text"
                placeholder="Search by Flight ID"
                className="mr-2"
                value={searchTerm}
                onChange={handleSearch}
              />
              <Button variant="outline-success">Search</Button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <div className="main">
        {flights.map((flight) => (
          <CardComponent key={flight.id} flight={flight} />
        ))}
      </div>
    </div>
  );
}

export default App;
