import React, { useEffect, useState } from "react";
import CardComponent from "./components/CardComponent";
import HeaderComponent from "./components/header";
import "./styles/app.css";
import { Container, Navbar, Nav, Form, Modal, Button } from "react-bootstrap";

function App() {
  const [flights, setFlights] = useState([]);
  const [filteredFlights, setFilteredFlights] = useState([]); 
  const [searchTerm, setSearchTerm] = useState("");
  const [show, setShow] = useState(false);
  const [newFlight, setNewFlight] = useState({ // Added
    flight_id: "",
    airline: "",
    source: "",
    destination: "",
    departure_time: "",
    arrival_time: "",
    seats: ""
  });

  useEffect(() => {
    fetch("http://localhost:3000/flights")
      .then((response) => response.json())
      .then((data) => {
        const validFlights = data.filter((flight) => flight.id);
        setFlights(validFlights);
        setFilteredFlights(validFlights);
      })
      .catch((error) => console.error("Error fetching flights:", error));
  }, []);

  const handleSearch = () => {
    const normalizedSearchTerm = searchTerm.trim().toLowerCase();

    if (normalizedSearchTerm === "") {
      setFilteredFlights(flights);
    } else {
      const filteredFlights = flights.filter(
        (flight) =>
          flight.flight_id.toLowerCase().includes(normalizedSearchTerm) ||
          flight.airline.toLowerCase().includes(normalizedSearchTerm) ||
          flight.source.toLowerCase().includes(normalizedSearchTerm) ||
          flight.destination.toLowerCase().includes(normalizedSearchTerm) ||
          flight.departure_time.toLowerCase().includes(normalizedSearchTerm) ||
          flight.arrival_time.toLowerCase().includes(normalizedSearchTerm)
      );

      setFilteredFlights(filteredFlights);
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      event.preventDefault();
      handleSearch();
    }
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleAddFlight = () => {
    setShow(true); // Added
  };

  const handleModalClose = () => { // Added
    setShow(false);
  };

  const handleNewFlightChange = (event) => { // Added
    const { name, value } = event.target;
    setNewFlight((prevFlight) => ({
      ...prevFlight,
      [name]: value
    }));
  };

  const handleNewFlightSubmit = () => { // Added
    fetch("http://localhost:3000/flights", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ ...newFlight, id: Math.random().toString(36).substring(7) })
    })
      .then((response) => response.json())
      .then((data) => {
        setFlights((prevFlights) => [...prevFlights, data]);
        setFilteredFlights((prevFlights) => [...prevFlights, data]);
        setShow(false);
        setNewFlight({
          flight_id: "",
          airline: "",
          source: "",
          destination: "",
          departure_time: "",
          arrival_time: "",
          seats: ""
        });
      })
      .catch((error) => console.error("Error adding flight:", error));
  };


  return (
    <div>
      <div>
        <HeaderComponent />
      </div>
      <Navbar expand="lg" className="bg-body-tertiary navbar-custom">
        <Container fluid>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="me-auto my-2 my-lg-0"
              style={{ maxHeight: "100px" }}
              navbarScroll
            >
              <button className="button-24" onClick={handleAddFlight}>
                Add Flight
              </button>
            </Nav>
            <Form className="d-flex" onSubmit={handleSearch}>
              <Form.Control
                type="search"
                placeholder="Search by Flight ID"
                className="me-2"
                aria-label="Search"
                value={searchTerm}
                onChange={handleInputChange}
                onKeyDown={handleKeyDown}
              />
              <button
                className="button-33"
                type="button"
                onClick={handleSearch}
              >
                Search
              </button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <div className="main">
        {filteredFlights.map((flight) => (
          <CardComponent key={flight.id} flight={flight} />
        ))}
      </div>

      <Modal show={show} onHide={handleModalClose}> {/* Added */}
        <Modal.Header closeButton>
          <Modal.Title>Add New Flight</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formFlightID">
              <Form.Label>Flight ID</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter Flight ID"
                name="flight_id"
                value={newFlight.flight_id}
                onChange={handleNewFlightChange}
              />
            </Form.Group>
            <Form.Group controlId="formAirline">
              <Form.Label>Airline</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter Airline"
                name="airline"
                value={newFlight.airline}
                onChange={handleNewFlightChange}
              />
            </Form.Group>
            <Form.Group controlId="formSource">
              <Form.Label>Source</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter Source"
                name="source"
                value={newFlight.source}
                onChange={handleNewFlightChange}
              />
            </Form.Group>
            <Form.Group controlId="formDestination">
              <Form.Label>Destination</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter Destination"
                name="destination"
                value={newFlight.destination}
                onChange={handleNewFlightChange}
              />
            </Form.Group>
            <Form.Group controlId="formDepartureTime">
              <Form.Label>Departure Time</Form.Label>
              <Form.Control
                type="datetime-local"
                name="departure_time"
                value={newFlight.departure_time}
                onChange={handleNewFlightChange}
              />
            </Form.Group>
            <Form.Group controlId="formArrivalTime">
              <Form.Label>Arrival Time</Form.Label>
              <Form.Control
                type="datetime-local"
                name="arrival_time"
                value={newFlight.arrival_time}
                onChange={handleNewFlightChange}
              />
            </Form.Group>
            <Form.Group controlId="formSeats">
              <Form.Label>Seats</Form.Label>
              <Form.Control
                type="number"
                placeholder="Enter number of seats"
                name="seats"
                value={newFlight.seats}
                onChange={handleNewFlightChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleModalClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleNewFlightSubmit}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>

    </div>
  );
}

export default App;
