import React, { useState, useEffect } from "react";
import { Navbar, Nav, Container, Form, FormControl, Button } from 'react-bootstrap';
import CardComponent from "./components/CardComponent";
import HeaderComponent from "./components/header";
import "./styles/app.css";

function App() {
  const [flights, setFlights] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetch("http://localhost:3000/flights") // Adjust the URL to your JSON server
      .then((response) => response.json())
      .then((data) => setFlights(data))
      .catch((error) => console.error("Error fetching flights:", error));
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value.toLowerCase());
  };

  const filteredFlights = flights.filter((flight) =>
    flight.origin.toLowerCase().includes(searchTerm) ||
    flight.destination.toLowerCase().includes(searchTerm) ||
    flight.airline.toLowerCase().includes(searchTerm)
  );

  return (
    <div>
      <Navbar bg="light" expand="lg">
        <Container fluid>
          <Navbar.Brand href="#">Flight Management</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="#">Add Flight</Nav.Link> {/* Add functionality for adding flights */}
              <Nav.Link href="#">View Flights</Nav.Link>  {/* Display all flights by default */}
              <Nav.Link href="#">Search Flights</Nav.Link>
            </Nav>
            <Form className="d-flex">
              <FormControl
                type="search"
                placeholder="Search Flights"
                className="me-2"
                aria-label="Search"
                value={searchTerm}
                onChange={handleSearch}
              />
              <Button variant="outline-success">Search</Button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <div className="main">
        {filteredFlights.map((flight) => (
          <CardComponent key={flight.id} flight={flight} />
        ))}
      </div>
    </div>
  );
}

export default App;
