import React, { useEffect, useState } from "react";
import HeaderComponent from "./header.js";
import "../styles/BookFlight.css";

function BookFlight() {
  const [flights, setFlights] = useState([]);
  const [passengers, setPassengers] = useState([]);
  const [selectedFlight, setSelectedFlight] = useState("");
  const [selectedPassenger, setSelectedPassenger] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  useEffect(() => {
    // Fetch flights and passengers data from API
    fetch("/path/to/api")
      .then(response => response.json())
      .then(data => {
        setFlights(data.flights);
        setPassengers(data.passengers);
      });
  }, []);

  const handleBooking = (e) => {
    e.preventDefault();
    // Handle the booking logic here
    console.log({
      email,
      password,
      flight_id: selectedFlight,
      passenger_id: selectedPassenger
    });
  };

  return (
    <div>
      <HeaderComponent />
      
      <div className="bookflight-container">
        <form className="bookflight-inputs" onSubmit={handleBooking}>
          <label className="bookflight-label">Email</label>
          <input 
            className="bookflight-input" 
            type="email" 
            placeholder="example@test.com" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <label className="bookflight-label">Password</label>
          <input 
            className="bookflight-input" 
            type="password" 
            placeholder="Min 6 characters long" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          <label className="bookflight-label">Flight</label>
          <select 
            className="bookflight-input" 
            value={selectedFlight}
            onChange={(e) => setSelectedFlight(e.target.value)}
          >
            <option value="">Select Flight</option>
            {flights.map(flight => (
              <option key={flight.id} value={flight.id}>
                {flight.airline} - {flight.source} to {flight.destination}
              </option>
            ))}
          </select>

          <label className="bookflight-label">Passenger</label>
          <select 
            className="bookflight-input" 
            value={selectedPassenger}
            onChange={(e) => setSelectedPassenger(e.target.value)}
          >
            <option value="">Select Passenger</option>
            {passengers.map(passenger => (
              <option key={passenger.id} value={passenger.id}>
                {passenger.name} - {passenger.passport_number}
              </option>
            ))}
          </select>

          <button className="bookflight-button" type="submit">Book Flight</button>
        </form>
      </div>
    </div>
  );
}

export default BookFlight;
