import React, { useEffect, useState } from "react";
import CardComponent from "./components/CardComponent";
import HeaderComponent from "./components/header";
import "./styles/app.css";
import {
  Container,
  Navbar,
  Nav,
  Form
} from "react-bootstrap";

function App() {
  const [flights, setFlights] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/flights") // Adjust the URL to your JSON server
      .then((response) => response.json())
      .then((data) => setFlights(data.filter((flight) => flight.id)))
      .catch((error) => console.error("Error fetching flights:", error));
  }, []);

  const handleSearch = (event) => {
    const searchTerm = event.target.value.trim().toLowerCase(); // Get the search term and normalize it

    // Filter flights based on the search term
    const filteredFlights = flights.filter((flight) =>
      flight.flight_id.toLowerCase().includes(searchTerm) ||
      flight.airline.toLowerCase().includes(searchTerm) ||
      flight.source.toLowerCase().includes(searchTerm) ||
      flight.destination.toLowerCase().includes(searchTerm) ||
      flight.departure_time.toLowerCase().includes(searchTerm) ||
      flight.arrival_time.toLowerCase().includes(searchTerm)
    );

    setFlights(filteredFlights); // Update flights state with filtered results
  };


  const handleAddFlight = () => {
    // Implement logic for adding a new flight
    // Redirect to a new page or modal for adding flight details
  };

  return (
    <div>
      <div>
        <HeaderComponent />
      </div>
      <Navbar expand="lg" className="bg-body-tertiary searh-bar">
        <Container fluid>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="me-auto my-2 my-lg-0"
              style={{ maxHeight: "100px" }}
              navbarScroll
            >
            <button class="button-24" href="#action1">
              Add Flight
            </button>
            </Nav>
            <Form className="d-flex">
              <Form.Control
                type="search"
                placeholder="Search by Flight ID"
                className="me-2"
                aria-label="Search"
                onChange={handleSearch}
              />
              <button class="button-33" >Search</button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <div className="main">
        {flights.map((flight) => (
          <CardComponent key={flight.id} flight={flight} />
        ))}
      </div>
    </div>
  );
}

export default App;
