// Bookings.js

import React, { useEffect, useState } from "react";
import HeaderComponent from "../components/header";
import BookingCardComponent from "../components/BookingCardComponent"; // Create this component similar to PassengerCardComponent
import BookingNavbar from "../components/BookingNavbar"; // Import AppNavbar
import ValidationModal from "../modals/ValidationModal"; // Import ValidationModal component
import "../styles/bookings.css"; // Create styles specific to bookings if needed

function Bookings() {
  const [bookings, setBookings] = useState([]);
  const [filteredBookings, setFilteredBookings] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [show, setShow] = useState(false);
  const [showValidationModal, setShowValidationModal] = useState(false);
  const [newBooking, setNewBooking] = useState({
    booking_id: "",
    flight_id: "",
    passenger_id: "",
    booking_date: "",
    status: "",
  });

  useEffect(() => {
    fetch("http://localhost:3000/bookings")
      .then((response) => response.json())
      .then((data) => {
        const validBookings = data.filter((booking) => booking.id);
        setBookings(validBookings);
        setFilteredBookings(validBookings);
      })
      .catch((error) => console.error("Error fetching bookings:", error));
  }, []);

  const handleSearch = () => {
    const normalizedSearchTerm = searchTerm.trim().toLowerCase();

    if (normalizedSearchTerm === "") {
      setFilteredBookings(bookings);
    } else {
      const filtered = bookings.filter(
        (booking) =>
          booking.booking_id.toLowerCase().includes(normalizedSearchTerm) ||
          booking.flight_id.toLowerCase().includes(normalizedSearchTerm) ||
          booking.passenger_id.toLowerCase().includes(normalizedSearchTerm)
      );

      setFilteredBookings(filtered);
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      event.preventDefault();
      handleSearch();
    }
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleAddBooking = () => {
    setShow(true);
  };

  const handleModalClose = () => {
    setShow(false);
  };

  const handleValidationModalClose = () => {
    setShowValidationModal(false);
  };

  const handleNewBookingChange = (event) => {
    const { name, value } = event.target;
    setNewBooking((prevBooking) => ({
      ...prevBooking,
      [name]: value,
    }));
  };

  const handleNewBookingSubmit = () => {
    const { booking_id, flight_id, passenger_id, booking_date, status } = newBooking;

    if (!booking_id || !flight_id || !passenger_id || !booking_date || !status) {
      setShowValidationModal(true);
      return;
    }

    fetch("http://localhost:3000/bookings", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        ...newBooking,
        id: Math.random().toString(36).substring(7),
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        setBookings((prevBookings) => [...prevBookings, data]);
        setFilteredBookings((prevBookings) => [...prevBookings, data]);
        setShow(false);
        setNewBooking({
          booking_id: "",
          flight_id: "",
          passenger_id: "",
          booking_date: "",
          status: "",
        });
      })
      .catch((error) => console.error("Error adding booking:", error));
  };

  return (
    <div>
      <div>
        <HeaderComponent />
      </div>
      <BookingNavbar
        handleAddBooking={handleAddBooking}
        searchTerm={searchTerm}
        handleInputChange={handleInputChange}
        handleKeyDown={handleKeyDown}
        handleSearch={handleSearch}
      />
      <div className="main">
        {filteredBookings.map((booking) => (
          <BookingCardComponent key={booking.id} booking={booking} />
        ))}
      </div>

      <AddBookingModal
        show={show}
        handleClose={handleModalClose}
        newBooking={newBooking}
        handleNewBookingChange={handleNewBookingChange}
        handleNewBookingSubmit={handleNewBookingSubmit}
      />

      <ValidationModal show={showValidationModal} handleClose={handleValidationModalClose} />
    </div>
  );
}

export default Bookings;
