import React from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import { AiOutlineCloseCircle } from "react-icons/ai"; // Import cross icon
import "../styles/BookingCardComponent.css"; // Import your CSS file

function BookingCardComponent({ booking, onCancelBooking }) {
  const { booking_id, flight_id, passenger_id, booking_date, status } = booking;

  const handleCancelBooking = () => {
    // Call a function to delete booking data from API
    onCancelBooking(booking_id); // Pass booking_id to identify which booking to cancel
  };

  return (
    <Card className="booking-card">
      <Card.Body>
        <Card.Title>
          Booking ID: {booking_id}{" "}
          <Button
            className="cancel-button"
            variant="link"
            onClick={handleCancelBooking}
          >
            <AiOutlineCloseCircle className="cancel-icon" />
          </Button>
        </Card.Title>
        <Card.Text>
          <strong>Flight ID:</strong> {flight_id}
          <br />
          <strong>Passenger ID:</strong> {passenger_id}
          <br />
          <strong>Booking Date:</strong>{" "}
          {new Date(booking_date).toLocaleString()}
          <br />
          <strong>Status:</strong> {status}
        </Card.Text>
      </Card.Body>
    </Card>
  );
}

export default BookingCardComponent;
