import React, { useEffect, useState } from "react";
import CardComponent from "./components/CardComponent";
import HeaderComponent from "./components/header";
import { Container, Navbar, Nav, Form, FormControl, Button } from "react-bootstrap";
import "./styles/app.css";

function App() {
  const [flights, setFlights] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetch("http://localhost:3000/flights") // Adjust the URL to your JSON server
      .then((response) => response.json())
      .then((data) => setFlights(data))
      .catch((error) => console.error("Error fetching flights:", error));
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleAddFlight = () => {
    // Implement logic for adding a new flight
    // Redirect to a new page or modal for adding flight details
  };

  const filteredFlights = flights.filter((flight) =>
    flight.id
  );


  return (
    <div>
      <HeaderComponent />
      <Navbar bg="light" expand="lg" className="secondary-navbar">
        <Container>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link onClick={handleAddFlight}>Add Flight</Nav.Link>
            </Nav>
            <Form className="d-flex">
              <FormControl
                type="text"
                placeholder="Search by Flight ID"
                className="mr-2"
                value={searchTerm}
                onChange={handleSearch}
              />
              <Button variant="outline-success">Search</Button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <Container className="main">
        {filteredFlights.map((flight) => (
          <CardComponent key={flight.id} flight={flight} />
        ))}
      </Container>
    </div>
  );
}

export default App;
