import React, { useEffect, useState } from "react";
import CardComponent from "./components/CardComponent";
import HeaderComponent from "./components/header";
import "./styles/app.css";
import { Container, Navbar, Nav, Form } from "react-bootstrap";

function App() {
  const [flights, setFlights] = useState([]);
  const [filteredFlights, setFilteredFlights] = useState([]); // Separate state for filtered flights
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetch("http://localhost:3000/flights")
      .then((response) => response.json())
      .then((data) => {
        const validFlights = data.filter((flight) => flight.id); // Filter out flights without an ID
        setFlights(validFlights);
        setFilteredFlights(validFlights); // Initialize filtered flights with all valid flights
      })
      .catch((error) => console.error("Error fetching flights:", error));
  }, []);

  const handleSearch = () => {
    const normalizedSearchTerm = searchTerm.trim().toLowerCase();

    if (normalizedSearchTerm === "") {
      setFilteredFlights(flights); // Reset to all flights if search term is empty
    } else {
      // Filter flights based on the search term
      const filteredFlights = flights.filter(
        (flight) =>
          flight.flight_id.toLowerCase().includes(normalizedSearchTerm) ||
          flight.airline.toLowerCase().includes(normalizedSearchTerm) ||
          flight.source.toLowerCase().includes(normalizedSearchTerm) ||
          flight.destination.toLowerCase().includes(normalizedSearchTerm) ||
          flight.departure_time.toLowerCase().includes(normalizedSearchTerm) ||
          flight.arrival_time.toLowerCase().includes(normalizedSearchTerm)
      );

      setFilteredFlights(filteredFlights); // Update filtered flights state with filtered results
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      event.preventDefault();
      handleSearch();
    }
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleAddFlight = () => {
    // Implement logic for adding a new flight
  };

  return (
    <div>
      <div>
        <HeaderComponent />
      </div>
      <Navbar expand="lg" className="bg-body-tertiary navbar-custom">
        <Container fluid>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="me-auto my-2 my-lg-0"
              style={{ maxHeight: "100px" }}
              navbarScroll
            >
              <button className="button-24" onClick={handleAddFlight}>
                Add Flight
              </button>
            </Nav>
            <Form className="d-flex" onSubmit={handleSearch}>
              <Form.Control
                type="search"
                placeholder="Search by Flight ID"
                className="me-2"
                aria-label="Search"
                value={searchTerm}
                onChange={handleInputChange}
                onKeyDown={handleKeyDown}
              />
              <button
                className="button-33"
                type="button"
                onClick={handleSearch}
              >
                Search
              </button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <div className="main">
        {filteredFlights.map((flight) => (
          <CardComponent key={flight.id} flight={flight} />
        ))}
      </div>
    </div>
  );
}

export default App;
