// Bookings.js

import React, { useEffect, useState } from "react";
import HeaderComponent from "../components/header";
import BookingCardComponent from "../components/BookingCardComponent"; // Create this component similar to PassengerCardComponent
import BookingNavbar from "../components/BookingNavbar"; // Import BookingNavbar
import ValidationModal from "../modals/ValidationModal"; // Import ValidationModal component
import { useHistory } from "react-router-dom"; // Import useHistory from react-router-dom for navigation
import "../styles/bookings.css"; // Create styles specific to bookings if needed

function Bookings() {
  const [bookings, setBookings] = useState([]);
  const [filteredBookings, setFilteredBookings] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [showValidationModal, setShowValidationModal] = useState(false);
  const history = useHistory();

  useEffect(() => {
    fetch("http://localhost:3000/bookings")
      .then((response) => response.json())
      .then((data) => {
        const validBookings = data.filter((booking) => booking.id);
        setBookings(validBookings);
        setFilteredBookings(validBookings);
      })
      .catch((error) => console.error("Error fetching bookings:", error));
  }, []);

  const handleSearch = () => {
    const normalizedSearchTerm = searchTerm.trim().toLowerCase();

    if (normalizedSearchTerm === "") {
      setFilteredBookings(bookings);
    } else {
      const filtered = bookings.filter(
        (booking) =>
          booking.booking_id.toLowerCase().includes(normalizedSearchTerm) ||
          booking.flight_id.toLowerCase().includes(normalizedSearchTerm) ||
          booking.passenger_id.toLowerCase().includes(normalizedSearchTerm)
      );

      setFilteredBookings(filtered);
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      event.preventDefault();
      handleSearch();
    }
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleAddBooking = () => {
    // Navigate to the "Book a Flight" page
    history.push("/book-a-flight");
  };

  return (
    <div>
      <div>
        <HeaderComponent />
      </div>
      <BookingNavbar
        handleAddBooking={handleAddBooking}
        searchTerm={searchTerm}
        handleInputChange={handleInputChange}
        handleKeyDown={handleKeyDown}
        handleSearch={handleSearch}
      />
      <div className="main">
        {filteredBookings.map((booking) => (
          <BookingCardComponent key={booking.id} booking={booking} />
        ))}
      </div>

      <ValidationModal show={showValidationModal} handleClose={() => setShowValidationModal(false)} />
    </div>
  );
}

export default Bookings;
