import React, { useState } from "react";
import { Container, Navbar, Nav, Form } from "react-bootstrap";
import HeaderComponent from "./header";
import ValidationModal from "../modals/ValidationModal";
import CardComponent from "../components/CardComponent";
import "../styles/passengers.css"; // Assuming you have a CSS file for passengers

function Passengers() {
  const [passengers, setPassengers] = useState([]);
  const [filteredPassengers, setFilteredPassengers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [showValidationModal, setShowValidationModal] = useState(false);

  // Assuming you have an API endpoint to fetch passengers
  useEffect(() => {
    fetch("http://localhost:3000/passengers")
      .then((response) => response.json())
      .then((data) => {
        setPassengers(data);
        setFilteredPassengers(data);
      })
      .catch((error) => console.error("Error fetching passengers:", error));
  }, []);

  const handleSearch = () => {
    const normalizedSearchTerm = searchTerm.trim().toLowerCase();

    if (normalizedSearchTerm === "") {
      setFilteredPassengers(passengers);
    } else {
      const filtered = passengers.filter((passenger) =>
        passenger.passenger_id.toLowerCase().includes(normalizedSearchTerm) ||
        passenger.name.toLowerCase().includes(normalizedSearchTerm) ||
        passenger.passport_number.toLowerCase().includes(normalizedSearchTerm)
      );
      setFilteredPassengers(filtered);
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      event.preventDefault();
      handleSearch();
    }
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleAddPassenger = () => {
    // Logic to handle adding a passenger
    // Show validation modal if needed
    setShowValidationModal(true);
  };

  const handleValidationModalClose = () => {
    setShowValidationModal(false);
  };

  return (
    <div>
      <HeaderComponent />
      <Navbar expand="lg" className="bg-body-tertiary navbar-custom">
        <Container fluid>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav className="me-auto my-2 my-lg-0" style={{ maxHeight: "100px" }} navbarScroll>
              <button className="button-24" onClick={handleAddPassenger}>
                Add Passenger
              </button>
            </Nav>
            <Form className="d-flex" onSubmit={handleSearch}>
              <Form.Control
                type="search"
                placeholder="Search by Passenger ID"
                className="me-2"
                aria-label="Search"
                value={searchTerm}
                onChange={handleInputChange}
                onKeyDown={handleKeyDown}
              />
              <button className="button-33" type="button" onClick={handleSearch}>
                Search
              </button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <div className="main">
        {filteredPassengers.map((passenger) => (
          <CardComponent key={passenger.id} passenger={passenger} />
        ))}
      </div>
      <ValidationModal show={showValidationModal} handleClose={handleValidationModalClose} />
    </div>
  );
}

export default Passengers;
