import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import { AiOutlineCloseCircle } from "react-icons/ai"; // Import cross icon
import "../styles/BookingCardComponent.css"; // Import your CSS file

function BookingCardComponent({ booking }) {
  const { booking_id, flight_id, passenger_id, booking_date, status } = booking;
  const [deleteSuccess, setDeleteSuccess] = useState(false);
  const [deleteError, setDeleteError] = useState(false);

  const handleCancelBooking = () => {
    fetch(`http://localhost:3000/bookings/${booking_id}`, {
      method: "DELETE",
    })
      .then((response) => {
        if (response.ok) {
          setDeleteSuccess(true);
          // Handle any UI updates or state changes after successful deletion
        } else {
          setDeleteError(true);
          console.error("Failed to cancel booking:", response.statusText);
        }
      })
      .catch((error) => {
        setDeleteError(true);
        console.error("Error canceling booking:", error);
      });
  };

  return (
    <Card className="booking-card">
      <Card.Body>
        <Card.Title>
          Booking ID: {booking_id}{" "}
          <Button
            className="cancel-button"
            variant="link"
            onClick={handleCancelBooking}
          >
            <AiOutlineCloseCircle className="cancel-icon" />
          </Button>
        </Card.Title>
        <Card.Text>
          <strong>Flight ID:</strong> {flight_id}
          <br />
          <strong>Passenger ID:</strong> {passenger_id}
          <br />
          <strong>Booking Date:</strong>{" "}
          {new Date(booking_date).toLocaleString()}
          <br />
          <strong>Status:</strong> {status}
        </Card.Text>
        {deleteSuccess && (
          <p className="text-success">Booking canceled successfully!</p>
        )}
        {deleteError && (
          <p className="text-danger">Failed to cancel booking. Please try again.</p>
        )}
      </Card.Body>
    </Card>
  );
}

export default BookingCardComponent;
