import React, { useEffect, useState } from "react";
import HeaderComponent from "../components/header";
import PassengerCardComponent from "../components/PassengerCardComponent"; // Create this component similar to CardComponent
import { Container, Navbar, Nav, Form, Modal, Button } from "react-bootstrap";
import "../styles/passengers.css"; // Create styles specific to passengers if needed

function Passengers() {
  const [passengers, setPassengers] = useState([]);
  const [filteredPassengers, setFilteredPassengers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [show, setShow] = useState(false);
  const [showValidationModal, setShowValidationModal] = useState(false);
  const [newPassenger, setNewPassenger] = useState({
    passenger_id: "",
    name: "",
    age: "",
    gender: "",
    passport_number: "",
  });

  useEffect(() => {
    fetch("http://localhost:3000/passengers")
      .then((response) => response.json())
      .then((data) => {
        const validPassengers = data.filter((passenger) => passenger.id);
        setPassengers(validPassengers);
        setFilteredPassengers(validPassengers);
      })
      .catch((error) => console.error("Error fetching passengers:", error));
  }, []);

  const handleSearch = () => {
    const normalizedSearchTerm = searchTerm.trim().toLowerCase();

    if (normalizedSearchTerm === "") {
      setFilteredPassengers(passengers);
    } else {
      const filtered = passengers.filter(
        (passenger) =>
          passenger.passenger_id.toLowerCase().includes(normalizedSearchTerm) ||
          passenger.name.toLowerCase().includes(normalizedSearchTerm)
      );

      setFilteredPassengers(filtered);
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      event.preventDefault();
      handleSearch();
    }
  };

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };  

  const handleAddPassenger = () => {
    setShow(true);
  };

  const handleModalClose = () => {
    setShow(false);
  };

  handleValidationModalClose

  const handleNewPassengerChange = (event) => {
    const { name, value } = event.target;
    setNewPassenger((prevPassenger) => ({
      ...prevPassenger,
      [name]: value,
    }));
  };

  const handleNewPassengerSubmit = () => {
    const { passenger_id, name, age, gender, passport_number } = newPassenger;

    if (!passenger_id || !name || !age || !gender || !passport_number) {
      setShowValidationModal(true);
      return;
    }

    fetch("http://localhost:3000/passengers", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        ...newPassenger,
        id: Math.random().toString(36).substring(7),
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        setPassengers((prevPassengers) => [...prevPassengers, data]);
        setFilteredPassengers((prevPassengers) => [...prevPassengers, data]);
        setShow(false);
        setNewPassenger({
          passenger_id: "",
          name: "",
          age: "",
          gender: "",
          passport_number: "",
        });
      })
      .catch((error) => console.error("Error adding passenger:", error));
  };

  return (
    <div>
      <div>
        <HeaderComponent />
      </div>
      <Navbar expand="lg" className="bg-body-tertiary navbar-custom">
        <Container fluid>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <Nav
              className="me-auto my-2 my-lg-0"
              style={{ maxHeight: "100px" }}
              navbarScroll
            >
              <button className="button-24" onClick={handleAddPassenger}>
                Add Passenger
              </button>
            </Nav>
            <Form className="d-flex" onSubmit={handleSearch}>
              <Form.Control
                type="search"
                placeholder="Search by Passenger ID or Name"
                className="me-2"
                aria-label="Search"
                value={searchTerm}
                onChange={handleInputChange}
                onKeyDown={handleKeyDown}
              />
              <button
                className="button-33"
                type="button"
                onClick={handleSearch}
              >
                Search
              </button>
            </Form>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <div className="main">
        {filteredPassengers.map((passenger) => (
          <PassengerCardComponent key={passenger.id} passenger={passenger} />
        ))}
      </div>

      <Modal show={show} onHide={handleModalClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add New Passenger</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formPassengerID">
              <Form.Label>Passenger ID</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter Passenger ID"
                name="passenger_id"
                value={newPassenger.passenger_id}
                onChange={handleNewPassengerChange}
              />
            </Form.Group>
            <Form.Group controlId="formName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter Name"
                name="name"
                value={newPassenger.name}
                onChange={handleNewPassengerChange}
              />
            </Form.Group>
            <Form.Group controlId="formAge">
              <Form.Label>Age</Form.Label>
              <Form.Control
                type="number"
                placeholder="Enter Age"
                name="age"
                value={newPassenger.age}
                onChange={handleNewPassengerChange}
              />
            </Form.Group>
            <Form.Group controlId="formGender">
              <Form.Label>Gender</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter Gender"
                name="gender"
                value={newPassenger.gender}
                onChange={handleNewPassengerChange}
              />
            </Form.Group>
            <Form.Group controlId="formPassportNumber">
              <Form.Label>Passport Number</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter Passport Number"
                name="passport_number"
                value={newPassenger.passport_number}
                onChange={handleNewPassengerChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleModalClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleNewPassengerSubmit}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>

      <Modal show={showValidationModal} onHide={handleValidationModalClose} style={{width:"450px",marginLeft:"535px", marginTop:"200px"}}>
        <Modal.Header closeButton style={{backgroundColor :"#a9a9a9"}}>
          <Modal.Title>Validation Error</Modal.Title>
        </Modal.Header>
        <Modal.Body style={{backgroundColor :"#a9a9a9"}}>Please fill in all the fields &#129398;</Modal.Body>
        <Modal.Footer style={{backgroundColor :"#a9a9a9"}}>
          <Button variant="secondary" onClick={handleValidationModalClose} style={{backgroundColor :"#212529"}}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Passengers;
